function init(){const elements=document.querySelectorAll("path");function move(p){if(p)elements.forEach(e=>{e.style.transform=`translate(${randomIntFromInterval(-2e3,-5e3)}px,${randomIntFromInterval(-2e3,-5e3)}px)`});else{const arr=Array.from(elements);for(let i=0;i<arr.length;i++)setTimeout(()=>{arr[i].style.removeProperty("transform")},100*i)}}function randomIntFromInterval(min,max){return Math.floor(Math.random()*(max-min+1)+min)}move(1),document.addEventListener("click",e=>{document.getElementsByClassName("hidden")[0].classList.remove("hidden"),setTimeout(()=>move(),1e3)},{once:!0})}document.addEventListener("DOMContentLoaded",init,{once:!0}),document.getElementById("close").addEventListener("click",e=>e.currentTarget.parentElement.className="hidden");